import React, { useReducer } from "react";

import Card from "../UI/Card/Card";
import classes from "./Login.module.css";
import Button from "../UI/Button/Button";

const formReducer = (state, action) => {
  if (action.mode === "email") {
    console.log("email", action.val, action.val.includes("@"));
    if (action.type === "USER_INPUT") {
      return {
        emailValue: action.val,
        isValid: action.val.includes("@"),
      };
    }

    if (action.type === "EMAIL_BLUR") {
      return {
        emailValue: state.val,
        isValid: state.val.includes("@"),
      };
    }
  }

  if (action.mode === "password") {
    console.log("email", action.val, action.val.trim().length > 6);
    if (action.type === "USER_INPUT") {
      return {
        passwordValue: action.val,
        isValid: action.val.trim().length > 6,
      };
    }

    if (action.type === "PASSWORD_BLUR") {
      return {
        passwordValue: state.val,
        isValid: state.val.trim().length > 6,
      };
    }
  }

  if (action.mode === "INPUT_RESET") {
    console.log("reset");
    return {
      emailValue: "",
      passwordValue: "",
      isValid: true,
    };
  }

  return {
    emailValue: "",
    passwordValue: "",
    isValid: true,
  };
};

const Login = (props) => {
  const [emailState, dispatchEmail] = useReducer(formReducer, {
    emailValue: "",
    isValid: false,
  });

  const [passwordState, dispatchPassword] = useReducer(formReducer, {
    passwordValue: "",
    isValid: false,
  });

  const [formState, dispatchResetForm] = useReducer(formReducer, {
    emailValue: "",
    passwordValue: "",
    isValid: false,
  });

  // Without dependencies array, this code below will run every single time when state changes, adding dependecies will ensure only it runs once when component renders
  // useEffect(() => {
  //   console.log("USEEFFECT running without dependency!");

  //   return () => {
  //     console.log("USE EFFECT CLEANUP running without dependency");
  //   };
  // });

  const emailChangeHandler = (event) => {
    dispatchEmail({
      type: "USER_INPUT",
      mode: "email",
      val: event.target.value,
    });
  };

  const passwordChangeHandler = (event) => {
    dispatchPassword({
      type: "USER_INPUT",
      mode: "password",
      val: event.target.value,
    });
  };

  const validateEmailHandler = () => {
    dispatchEmail({
      type: "EMAIL_BLUR",
    });
  };

  const validatePasswordHandler = () => {
    dispatchPassword({
      type: "PASSWORD_BLUR",
    });
  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(emailState.emailValue, passwordState.passwordValue);
  };

  const clearFormHandler = () => {
    dispatchResetForm({
      type: "INPUT_RESET",
      mode: "reset",
    });
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${
            emailState.isValid === false ? classes.invalid : ""
          }`}
        >
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={emailState.emailValue}
            onChange={emailChangeHandler}
            onBlur={validateEmailHandler}
          />
        </div>
        <div
          className={`${classes.control} ${
            passwordState.isValid === false ? classes.invalid : ""
          }`}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={passwordState.passwordValue}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button
            type="submit"
            className={classes.btn}
            disabled={!formState.isValid}
          >
            Login
          </Button>
          &nbsp;
          <Button
            type="reset"
            className={classes.btn}
            onClick={clearFormHandler}
          >
            Reset
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
