import React, { useContext } from "react";

import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import ButtonList from "../UI/Button/ButtonList";

import classes from "./Home.module.css";
import AuthContext from "../../store/auth-context";

const Home = () => {
  const authCtx = useContext(AuthContext);

  const BASE_URL = "https://beta.reactjs.org/reference/react/";

  const listData = [
    {
      btnContent: `useState()`,
      href: BASE_URL + "useState",
    },
    {
      btnContent: `useEffect()`,
      href: BASE_URL + "useEffect",
    },
    {
      btnContent: `useReducer()`,
      href: BASE_URL + "useReducer",
    },
    {
      content: `useContext()`,
      href: BASE_URL + "useContext",
    },
  ];

  return (
    <Card className={classes.home}>
      <h1>Welcome back {authCtx.name}!</h1>
      <br />
      <h2 style={{ fontWeight: "bold" }}>Lets learn about React Hooks</h2>
      <ButtonList lists={listData} />
      <Button style={{ textAlign: "center" }} onClick={authCtx.onLogout}>
        Logout
      </Button>
    </Card>
  );
};

export default Home;
